ֆունկցիա validateParens(str) {
  թող փակագծեր Counter = 0;
  համար (թող i = 0; i <str.length; i++) {
    եթե (str[i] === '(') {
      փակագծերՀաշիվ ++;
    } else if (str[i] === ')') {
      փակագծերՀաշիվ--;
    }

    եթե (փակագծերՀաշիվ < 0) {
      վերադարձ կեղծ;
    }
  }
  վերադարձնել փակագծերՀաշիվ === 0;
}

